package logicalprogramming;

public class Evenoroddnum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num = 103;
		if(num % 2 == 0) {
			System.out.println(num + " is even number");
		}
		else {
			System.out.println(num + " is odd number");
		}

	}

}
